mapper.select:
SELECT NA_WRS_C,WRSNM,WRS_STDNM,NA_WRS_UNT_C,NA_WRS_ATTNM,VCBT_UPR,VCBT_FEE,WRS_CLF,WRS_CLFNM,FSRG_DTM FROM ( SELECT A.NA_WRS_C ,A.WRSNM ,A.WRS_STDNM ,A.NA_WRS_UNT_C ,C.NA_WRS_ATTNM ,(SELECT MIN(VCBT_UPR) FROM TB_OD_TRTWRS WHERE NA_WRS_C= A.NA_WRS_C) VCBT_UPR ,E.VCBT_FEE ,A.NA_WRS_LCLC||A.NA_WRS_MCLC||A.NA_WRS_SCLC||A.NA_WRS_DTCF_C WRS_CLF ,B.WRS_LCLFNM || DECODE(WRS_MCLFNM,'','','/' || WRS_MCLFNM ) || DECODE(WRS_SCLFNM,'','','/' || WRS_SCLFNM ) || DECODE(WRS_DTCFNM,'','','/' || WRS_DTCFNM ) AS WRS_CLFNM ,TO_CHAR(A.FSRG_DTM, 'YYYYMMDD') AS FSRG_DTM FROM TB_GD_WRS A, TB_GD_WRS_CLF B, TB_GD_WRS_ATTR C, TB_CM_SIMPC D, TB_CM_VCBT_FEE E WHERE A.NA_WRS_LCLC=B.NA_WRS_LCLC AND A.NA_WRS_MCLC=B.NA_WRS_MCLC AND A.NA_WRS_SCLC=B.NA_WRS_SCLC AND A.NA_WRS_DTCF_C=B.NA_WRS_DTCF_C AND D.SIMP_TPC='TXT_DSC' AND A.TXT_DSC=D.SIMP_C AND C.NA_WRS_ATTR_C=A.NA_WRS_UNT_C AND C.WRS_ATTR_DSC='01' AND A.WRS_TPC=#{WRS_TPC} AND A.DEL_YN='0' AND A.NA_WRS_C=E.NA_WRS_C AND C.DEL_YN='0' AND E.DEL_YN='0'
AND A.NA_WRS_C = #{searchText}
AND A.WRSNM LIKE '${searchText}%'
) GROUP BY NA_WRS_C,WRSNM,WRS_STDNM,NA_WRS_UNT_C,NA_WRS_ATTNM,VCBT_UPR,VCBT_FEE,WRS_CLF,WRS_CLFNM,FSRG_DTM ORDER BY WRSNM ASC

if.if:

	SELECT  
	         NA_WRS_DTCF_C
	       , WRS_DTCFNM
           , WRS_ID_DSC
	 FROM TB_GD_WRS_CLF
	WHERE NA_WRS_LCLC = #{NA_WRS_LCLC}
	AND NA_WRS_MCLC   = #{NA_WRS_MCLC}
	AND NA_WRS_SCLC   = #{NA_WRS_SCLC}
	AND NA_WRS_LCLC   < '200'
	ORDER BY WRS_DTCFNM
