mapper.sql:
SELECT A.NA_WRS_C /* 상품코드 */ ,A.WRSNM /* 상품명 */ ,A.WRS_DSC /* 상품구분코드 */ ,FN_CM_SIMP_CNM('WRS_DSC', A.WRS_DSC) AS WRS_DSC_NM ,A.WRS_STDNM /* 규격 */ ,A.TXT_DSC /* 과세구분 */ ,FN_CM_SIMP_CNM('TXT_DSC', A.TXT_DSC) AS TXT_DSC_NM ,A.MFT_NA_TRPL_C /* 거래처코드 */ ,(SELECT CLNTNM FROM TB_TR_TRPL_CIF WHERE NA_TRPL_C = A.MFT_NA_TRPL_C )AS MFT_NA_TRPL_NM /* 거래처명 */ ,B.WRS_LCLFNM|| /* 분류 */ DECODE(B.WRS_MCLFNM,'','','/'||B.WRS_MCLFNM)|| DECODE(B.WRS_SCLFNM,'','','/'||B.WRS_SCLFNM)|| DECODE(B.WRS_DTCFNM,'','','/'||B.WRS_DTCFNM)AS NA_WRS_CLNM ,TO_CHAR(A.FSRG_DTM,'YYYY-MM-DD') AS FSRG_DTM /* 등록일 */ ,TO_CHAR(A.LSCHG_DTM,'YYYY-MM-DD') AS LSCHG_DTM /* 변경일 */ FROM TB_GD_WRS A,TB_GD_WRS_CLF B WHERE A.NA_WRS_LCLC = B.NA_WRS_LCLC(+) AND A.NA_WRS_MCLC = B.NA_WRS_MCLC(+) AND A.NA_WRS_SCLC = B.NA_WRS_SCLC(+) AND A.NA_WRS_DTCF_C = B.NA_WRS_DTCF_C(+) AND A.NA_WRS_C IN (
SELECT NA_WRS_C FROM TB_OD_TRTWRS WHERE SPYPL_NA_TRPL_C IN ( SELECT NA_TRPL_C FROM TB_TR_TRPL_CIF WHERE UP_NA_TRPL_C IN ( SELECT UP_NA_TRPL_C FROM TB_TR_TRPL_CIF WHERE NA_TRPL_C = #{TRPL_C} )
)
)
AND A.MFT_NA_TRPL_C = #{MNGT_C}
AND A.WRS_C_DSC IN
#{item2}
AND A.NA_WRS_C LIKE #{NA_WRS_C} || '%'
AND A.NA_WRS_LCLC = #{NA_WRS_LCLC} AND A.NA_WRS_MCLC = #{NA_WRS_MCLC} AND A.NA_WRS_SCLC = #{NA_WRS_SCLC} AND A.NA_WRS_DTCF_C = #{NA_WRS_DTCF_C}
AND A.WRS_DSC = #{WRS_DSC}
AND TO_CHAR(FSRG_DTM, 'YYYYMMDD') BETWEEN #{FROM_DATE} AND #{TO_DATE}
AND TO_CHAR(LSCHG_DTM, 'YYYYMMDD') BETWEEN #{FROM_DATE2} AND #{TO_DATE2}
ORDER BY A.NA_WRS_C ASC

