SELECT AA.VAN_C_RQ_NO, AA.NA_C_RQ_NO, AA.APV_RQ_NA_BZPLC, AA.GBN, AA.RES_CODE, AA.RQ_NA_WRS_C, AA.WRSNM, AA.FSRG_DTM, AA.REQ_DT, AA.FSRGMN_NA_BZPLC , FN_TRPL_CIF_NM(AA.FSRGMN_NA_BZPLC) AS FSRGMN_NA_BZPL_NM , AA.FSRGMN_ENO, AA.RES_DT, AA.BUYER_ENO, AA.BUYER_NM, AA.RSP_C_CNTN, AA.NA_C_RQ_REJ_RSNC, FN_CM_SIMP_CNM('NA_C_RQ_REJ_RSNC', AA.NA_C_RQ_REJ_RSNC) AS NA_C_RQ_REJ_RSNC_NM, AA.NA_C_RQ_STSC, FN_CM_SIMP_CNM('NA_C_RQ_STSC', AA.NA_C_RQ_STSC) AS NA_C_RQ_STSC_NM FROM ( SELECT A.VAN_C_RQ_NO , A.NA_C_RQ_NO , A.APV_RQ_NA_BZPLC , DECODE(A.CUD_DSC, 'I', '신청', 'U', '수정' ) AS GBN , CASE WHEN A.PRC_YN IN ('Y', 'R') THEN '정상' WHEN A.PRC_YN IS NULL THEN '' WHEN A.PRC_YN IN ('N', 'P') THEN '전송중' ELSE '오류' END RES_CODE , A.RQ_NA_WRS_C , A.WRSNM , A.FSRG_DTM , TO_CHAR(A.FSRG_DTM, 'YYYY-MM-DD') AS REQ_DT , A.FSRGMN_NA_BZPLC , A.FSRGMN_ENO , TO_CHAR(A.CNF_DMT, 'YYYY-MM-DD') AS RES_DT , A.CNF_ENO AS BUYER_ENO , A.CNF_NAME AS BUYER_NM , A.RSP_C_CNTN , A.EDI_IF_ID , A.NA_C_RQ_REJ_RSNC , A.NA_C_RQ_STSC FROM TB_GD_WRS_RG_REQ A WHERE 1=1
AND A.FSRGMN_NA_BZPLC IN
