SELECT CASE WHEN LENGTH('${NA_WRS_C}') = 13 AND REGEXP_COUNT('${NA_WRS_C}', '^(1|2)') > 0 THEN '10' WHEN LENGTH('${NA_WRS_C}') = 13 AND SF_NA_BS_GET_CHKDIGIT(SUBSTR('${NA_WRS_C}',1,12)) != SUBSTR('${NA_WRS_C}',-1) THEN '11' WHEN LENGTH('${NA_WRS_C}') = 14 AND SF_NA_BS_GET_CHKDIGIT(SUBSTR('${NA_WRS_C}',1,13)) != SUBSTR('${NA_WRS_C}',-1) THEN '11' ELSE '00' END AS verfDsc /*검증구분코드 */
, CASE WHEN LENGTH('${NA_WRS_C}') = 13 AND REGEXP_COUNT('${NA_WRS_C}', '^(1|2)') > 0 THEN '상품코드 체크' WHEN LENGTH('${NA_WRS_C}') = 13 AND SF_NA_BS_GET_CHKDIGIT(SUBSTR('${NA_WRS_C}',1,12)) != SUBSTR('${NA_WRS_C}',-1) THEN '상품코드 체크디지트 오류' WHEN LENGTH('${NA_WRS_C}') = 14 AND SF_NA_BS_GET_CHKDIGIT(SUBSTR('${NA_WRS_C}',1,13)) != SUBSTR('${NA_WRS_C}',-1) THEN '상품코드 체크디지트 오류' ELSE '정상' END AS verfBrk /*검증내역 */ FROM DUAL
