mapper.update:
UPDATE TB_OD_ORDER_D
SET

	NA_DVY_PLASH_SLPNO = #{NA_DVY_PLASH_SLPNO}

WHERE 1=1
	AND ODRPL_NA_TRPL_C = #{ODRPL_NA_TRPL_C}
	AND ODRPL_NA_TEAM_C = #{ODRPL_NA_TEAM_C}
	AND ODR_DT = #{ODR_DT}
	AND ODR_SLPNO = #{ODR_SLPNO}
	AND NA_WRS_C = #{NA_WRS_C}

mapper.select:
SELECT * FROM ( SELECT DM.SPYPL_NA_TRPL_C , DM.SPYPL_NA_TEAM_C , FN_TRPL_CIF_NM(DM.SPYPL_NA_TRPL_C) AS SPYPL_NA_TRPL_N , DM.DVYAA_NA_TRPL_C , DM.NA_DVY_PLASH_SLPNO , DM.ODRPL_NA_TRPL_C , DM.ODRPL_NA_TEAM_C , FN_TRPL_CIF_NM(DM.ODRPL_NA_TRPL_C) AS SHRT_BZPLNM , FN_CM_TEAM_NM(DM.ODRPL_NA_TRPL_C, DM.ODRPL_NA_TEAM_C) AS TEAMNM , DM.DVYAA_C , DM.CSER_CTR_TPC , DM.DVY_PLA_DT , DM.SLGT_ETR_DSC , DM.DVY_VHC_DSC , DM.VHCNO , DM.SPY_TPC , NVL(DM.DVY_AM, 0) AS TOTAL_DVY_AM , NVL(DM.DVY_VAT, 0) AS TOTAL_DVY_VAT , DM.DVY_FAR_ASTCS , DM.RVOPL_NA_TRPL_C , DM.RVOPL_NA_TEAM_C , DM.DVYAA_NA_TEAM_C , DM.ADJPL_NA_TRPL_C , DM.ADJPL_NA_TEAM_C , DM.MNGT_NA_TRPL_C , DM.MNGT_NA_TEAM_C , DM.MNGT_NA_TR_TPC , DM.DDLY_YN , DM.DVY_PLASH_STSC , FN_CM_SIMP_CNM('DVY_PLASH_STSC', DM.DVY_PLASH_STSC) AS DVY_PLASH_STSC_N , DM.NORDER_YN , CASE DM.DDLY_YN WHEN '0' THEN FN_CM_PRGR_NM(DM.ODRPL_NA_TRPL_C, DM.ODRPL_NA_TEAM_C, DM.SPYPL_NA_TRPL_C, DM.SPYPL_NA_TEAM_C, DM.CSER_CTR_TPC) ELSE FN_CM_PRGR_NM(DM.RVOPL_NA_TRPL_C, DM.RVOPL_NA_TEAM_C, DM.SPYPL_NA_TRPL_C, DM.SPYPL_NA_TEAM_C, DM.CSER_CTR_TPC) END AS PGNM , DM.PRC_YN , DM.TEMP_YN , DM.RMK_CNTN , DM.RES_DESC , ( SELECT OM.DVY_RQR_DT FROM TB_OD_ORDER_M OM WHERE DM.ODRPL_NA_TRPL_C = OM.ODRPL_NA_TRPL_C AND DM.ODRPL_NA_TEAM_C = OM.ODRPL_NA_TEAM_C AND DD.ODR_DT = OM.ODR_DT AND DD.ODR_SLPNO = OM.ODR_SLPNO AND DM.SPYPL_NA_TRPL_C = OM.SPYPL_NA_TRPL_C ) AS DVY_RQR_DT FROM TB_OD_DLVPNSHT_M DM INNER JOIN TB_OD_DLVPNSHT_D DD ON DM.SPYPL_NA_TRPL_C = DD.SPYPL_NA_TRPL_C AND DM.DVYAA_NA_TRPL_C = DD.DVYAA_NA_TRPL_C AND DM.NA_DVY_PLASH_SLPNO = DD.NA_DVY_PLASH_SLPNO AND DM.DVY_PLA_DT = DD.DVY_PLA_DT WHERE DM.SPYPL_NA_TRPL_C = #{SPYPL_NA_TRPL_C} AND DM.DVYAA_NA_TRPL_C = #{DVYAA_NA_TRPL_C} AND DM.NA_DVY_PLASH_SLPNO = #{NA_DVY_PLASH_SLPNO}
AND TRU_AGCY_NA_TRPL_C = #{TRU_AGCY_NA_TRPL_C}
) WHERE ROWNUM = 1 ORDER BY DVY_RQR_DT NULLS LAST

mapper.delete:
