DELETE 
FROM TB_OD_DLVPNSHT_D
WHERE 1=1
	AND SPYPL_NA_TRPL_C = #{SPYPL_NA_TRPL_C}
	AND DVYAA_NA_TRPL_C = #{DVYAA_NA_TRPL_C} 
	AND NA_DVY_PLASH_SLPNO = #{NA_DVY_PLASH_SLPNO}

mapper.sql:
SELECT /*+ index(TB_BY_BYNG_M IDX_BY_BYNG_M_05) */ DM.RVOPL_NA_TRPL_C, FN_TRPL_CIF_NM(DM.RVOPL_NA_TRPL_C) AS RVOPL_NA_TRPL_N, DM.DVYAA_NA_TRPL_C, FN_TRPL_CIF_NM(DM.DVYAA_NA_TRPL_C) AS DVYAA_NA_TRPL_N, DM.NA_DVY_PLASH_SLPNO, DM.DVY_PLA_DT, DM.DVY_AM, DM.DVY_VAT, DM.DVY_PLASH_STSC || ' ' ||FN_CM_SIMP_CNM('DVY_PLASH_STSC', DM.DVY_PLASH_STSC) AS DVY_PLA_STS, DM.CSER_CTR_TPC || ' ' ||FN_CM_SIMP_CNM('CSER_CTR_TPC', DM.CSER_CTR_TPC) AS CSER_CTR_TPC, DM.FSRGMN_ENO, MAX(DM.DVY_SSDY) AS DVY_SSDY, MAX(DD.DVY_SSDYRT) AS DVY_SSDYRT, BD.BYAM, BD.BYNG_QT, BM.SLP_DT, BM.NA_SLPNO, DM.ODRPL_NA_TRPL_C, FN_TRPL_CIF_NM(DM.ODRPL_NA_TRPL_C) AS ODRPL_NA_TRPL_N, DM.DDLY_YN, '[' || DM.ODRPL_NA_TEAM_C || ']' || FN_CM_TEAM_NM (DM.ODRPL_NA_TRPL_C, DM.ODRPL_NA_TEAM_C) AS ODRPL_NA_TEAM_N, CASE WHEN BD.NA_BZPLC IS NULL THEN DM.DVY_PLA_QT ELSE 0 END AS STS_DVY_PLA_QT, /* 배송예정수량 */ CASE WHEN BD.NA_BZPLC IS NULL THEN DM.DVY_AM ELSE 0 END AS STS_DVY_AM, CASE WHEN BD.NA_BZPLC IS NOT NULL THEN DM.DVY_AM ELSE 0 END AS STS_BYAM, CASE WHEN BD.NA_BZPLC IS NOT NULL THEN DM.DVY_PLA_QT ELSE 0 END AS STS_BYNG_QT, DM.GRS_SOJU_YN, DM.SPYPL_NA_TRPL_C, DM.LS_CMENO, DM.LSCHG_DTM, DM.RMK_CNTN, DM.SLPNO_RMS_DTM FROM TB_OD_DLVPNSHT_M DM, TB_OD_DLVPNSHT_D DD, (SELECT ACTL_BUYPL_NA_TRPL_C ,NA_BZPLC,NA_TEAM_C ,NA_DVY_PLASH_SLPNO ,SLP_DT ,NA_SLPNO FROM TB_BY_BYNG_M WHERE NA_CRC_CAN_DSC = 1 ) BM, (SELECT NA_BZPLC, NA_TEAM_C, SLP_DT, NA_SLPNO, SUM(BYAM) AS BYAM, SUM(BYNG_VAT) AS BYNG_VAT, SUM(BYNG_QT) AS BYNG_QT FROM TB_BY_BYNG_WRS GROUP BY NA_BZPLC, NA_TEAM_C, SLP_DT, NA_SLPNO) BD WHERE 1=1 AND DM.PRC_YN IN ('Y', 'R') AND DM.TEMP_YN = 'N' AND DM.SPYPL_NA_TRPL_C = DD.SPYPL_NA_TRPL_C AND DM.DVYAA_NA_TRPL_C = DD.DVYAA_NA_TRPL_C AND DM.NA_DVY_PLASH_SLPNO = DD.NA_DVY_PLASH_SLPNO AND DM.SPYPL_NA_TRPL_C = BM.ACTL_BUYPL_NA_TRPL_C(+) AND DM.ODRPL_NA_TRPL_C = BM.NA_BZPLC(+) AND DM.NA_DVY_PLASH_SLPNO = BM.NA_DVY_PLASH_SLPNO(+) AND BM.NA_BZPLC = BD.NA_BZPLC(+) AND BM.NA_TEAM_C = BD.NA_TEAM_C(+) AND BM.SLP_DT = BD.SLP_DT(+) AND BM.NA_SLPNO = BD.NA_SLPNO(+)
AND DM.TRU_AGCY_NA_TRPL_C = #{truAgcyNaTrplC}
AND DM.DVY_PLASH_STSC = #{dvy_pla_sts_rdo}
/* 배송예정서 번호 */ AND DM.NA_DVY_PLASH_SLPNO = #{na_dvy_plash_slpno}
/* 발주처 */ AND DM.ODRPL_NA_TRPL_C = #{odrpl_na_trpl_c}
/* 계약유형 */ AND DM.CSER_CTR_TPC = #{cser_ctr_tpc}
/* 배송구분 */ AND DM.DDLY_YN = #{ddly_yn}
/* 배송일자 */ AND DM.DVY_PLA_DT BETWEEN #{dvy_pla_dt_from} AND #{dvy_pla_dt_to}
/* 지방소주여부 */ AND DM.GRS_SOJU_YN = #{ngrs_soju_yn}
AND DM.SPYPL_NA_TRPL_C IN
#{item}
GROUP BY BD.BYAM, BD.BYNG_QT, DM.RVOPL_NA_TRPL_C, DM.DVYAA_NA_TRPL_C, DM.NA_DVY_PLASH_SLPNO, DM.DVY_PLA_DT, DM.DVY_AM, DM.DVY_VAT, DM.DVY_PLASH_STSC, DM.CSER_CTR_TPC, DM.FSRGMN_ENO, BM.SLP_DT, BM.NA_SLPNO, DM.ODRPL_NA_TRPL_C, DM.DDLY_YN, DM.ODRPL_NA_TEAM_C, DM.GRS_SOJU_YN, BD.NA_BZPLC, DM.DVY_PLA_QT, DM.SPYPL_NA_TRPL_C, DM.LS_CMENO, DM.LSCHG_DTM, DM.RMK_CNTN, DM.SLPNO_RMS_DTM

SELECT A.*, E.BYNG_UPR, /* 매입확정단가 */ E.BYNG_QT, /* 매입확정수량 */ E.BYNG_WT, /* 매입확정중량 */ E.BYAM /* 매입확정금액 */ FROM ( SELECT A.NA_WRS_C, C.WRSNM, A.ODR_DT, (A.ODR_DT || '-' || A.ODR_SLPNO || '-' || A.ODR_DSQNO) AS ODR_SLPNO, A.ODR_DSQNO, FN_CM_SIMP_CNM('TXT_DSC', A.TXT_DSC) AS TXT_DSC, /* 과세구분 */ '0000' AS PD_YY, /* 생산년도 */ A.DVY_WRS_UPR, /* 배송상품단가 */ A.DVY_PLA_QT, /* 배송예정수량 */ A.DVY_AM, /* 배송금액 */ A.DVY_VAT, /* 배송부가세 */ A.DVY_VCBT_AM, /* 배송공병금액 */ ( CASE WHEN A.DVY_SSDYRT <> 0 AND A.DVY_SSDY = 0 THEN ( A.DVY_AM * A.DVY_SSDYRT ) / 100 ELSE A.DVY_SSDY END ) AS DVY_SSDY, /* 배송장려금 */ A.DVY_TROT_FEE, /* 배송환급수수료 */ A.DVY_PHD_FEE, /* 배송물류수수료 */ A.DVY_FAR_ASTCS, /* 배송운임보조비 */ B.DVY_PLASH_STSC || FN_CM_SIMP_CNM('DVY_PLASH_STSC', B.DVY_PLASH_STSC) ||' ' AS DVY_PLASH_STAC, /* 배송예정서상태코드 */ A.DVY_SSDYRT, /* 배송장려금율 */ A.DVY_PLA_SQNO, B.DVY_PLA_DT, A.NA_DVY_PLASH_SLPNO, B.RVOPL_NA_TRPL_C, B.SPYPL_NA_TRPL_C, D.NA_BZPLC, D.NA_TEAM_C, D.SLP_DT, D.NA_SLPNO FROM TB_OD_DLVPNSHT_D A, TB_OD_DLVPNSHT_M B, TB_GD_WRS C, TB_BY_BYNG_M D WHERE 1=1 AND A.SPYPL_NA_TRPL_C = B.SPYPL_NA_TRPL_C AND A.DVYAA_NA_TRPL_C = B.DVYAA_NA_TRPL_C AND A.NA_DVY_PLASH_SLPNO = B.NA_DVY_PLASH_SLPNO AND A.NA_WRS_C = C.NA_WRS_C(+) AND A.WK_DS != 'X' AND A.WK_DS != 'D' AND B.SPYPL_NA_TRPL_C = D.ACTL_BUYPL_NA_TRPL_C(+) AND B.NA_DVY_PLASH_SLPNO = D.NA_DVY_PLASH_SLPNO(+) AND B.ODRPL_NA_TRPL_C = D.NA_BZPLC(+) AND A.SPYPL_NA_TRPL_C = #{SPYPL_NA_TRPL_C} AND A.DVYAA_NA_TRPL_C = #{DVYAA_NA_TRPL_C} AND A.NA_DVY_PLASH_SLPNO = #{NA_DVY_PLASH_SLPNO} AND 1 = D.NA_CRC_CAN_DSC(+)
AND TRU_AGCY_NA_TRPL_C = #{TRU_AGCY_NA_TRPL_C}
AND D.NA_SLPNO = #{NA_SLPNO}
AND D.SLP_DT = #{SLP_DT}
)A , TB_BY_BYNG_WRS E WHERE 1=1 AND A.NA_BZPLC = E.NA_BZPLC(+) AND A.NA_TEAM_C = E.NA_TEAM_C(+) AND A.SLP_DT = E.SLP_DT(+) AND A.NA_SLPNO = E.NA_SLPNO(+) AND A.DVY_PLA_SQNO = E.SLP_SQNO(+) AND A.NA_WRS_C = E.NA_WRS_C(+)

mapper.select:
SELECT A.NA_WRS_C, B.WRSNM,
B.WRS_STDNM NA_WRS_STD_UNT_N, FN_CM_SIMP_CNM('NA_WRS_UNT_C', B.NA_WRS_UNT_C) AS NA_WRS_UNT_N, NVL(B.BOXPE_AQZ, 1) AS BOXPE_AQZ, A.DVY_WRS_UPR, NVL(A.DVY_PLA_QT, 0) AS DVY_PLA_QT, NVL(A.DVY_AM, 0) AS DVY_AM, NVL(A.DVY_VAT, 0) AS DVY_VAT, NVL(D.VCBT_UPR, 0) AS VCBT_UPR, NVL(A.DVY_VCBT_AM, 0) AS DVY_VCBT_AM FROM ( SELECT A.NA_WRS_C, A.DVY_WRS_UPR, C.ODRPL_NA_TRPL_C, NVL(A.DVY_PLA_QT, 0) AS DVY_PLA_QT, NVL(A.DVY_AM, 0) AS DVY_AM, NVL(A.DVY_VAT, 0) AS DVY_VAT, NVL(A.DVY_VCBT_AM, 0) AS DVY_VCBT_AM FROM TB_OD_DLVPNSHT_D A, TB_OD_DLVPNSHT_M C WHERE 1=1 AND A.SPYPL_NA_TRPL_C = C.SPYPL_NA_TRPL_C AND A.DVYAA_NA_TRPL_C = C.DVYAA_NA_TRPL_C AND A.NA_DVY_PLASH_SLPNO = C.NA_DVY_PLASH_SLPNO AND A.WK_DS != 'D' AND A.DVYAA_NA_TRPL_C = #{DVYAA_NA_TRPL_C} AND A.SPYPL_NA_TRPL_C = #{SPYPL_NA_TRPL_C} AND A.NA_DVY_PLASH_SLPNO = #{NA_DVY_PLASH_SLPNO}
AND A.TRU_AGCY_NA_TRPL_C = #{TRU_AGCY_NA_TRPL_C}
) A, TB_GD_WRS B, TB_OD_TRTWRS D WHERE 1=1 AND A.NA_WRS_C = B.NA_WRS_C AND A.NA_WRS_C = D.NA_WRS_C(+) AND A.ODRPL_NA_TRPL_C = D.NA_BZPLC(+)

SELECT DISTINCT A.ODR_DT/* 발주일자 */, A.ODR_SLPNO /* 전표번호 */, A.CSER_CTR_TPC, '[' || A.CSER_CTR_TPC || ']' || FN_CM_SIMP_CNM ('CSER_CTR_TPC', A.CSER_CTR_TPC) AS CSER_CTR_TPC_N /* 계약구분 */, A.ODRPL_NA_TRPL_C /* 발주처코드 */, FN_TRPL_CIF_NM (A.ODRPL_NA_TRPL_C) AS ODRPL_NA_TRPL_N/* 발주처명 */, A.ODRPL_NA_TEAM_C /* 발주처팀코드 */, FN_CM_TEAM_NM (A.ODRPL_NA_TRPL_C, A.ODRPL_NA_TEAM_C) AS ODRPL_NA_TEAM_N /* 발주처팀명 */, '[' || A.DVY_VHC_DSC || ']' || FN_CM_SIMP_CNM ('DVY_VHC_DSC', A.DVY_VHC_DSC) AS DVY_VHC_DSC /* 배송구분 */, '[' || A.SPY_TPC || ']' || FN_CM_SIMP_CNM ('SPY_TPC', A.SPY_TPC) AS SPY_TPC_N /* 공급구분 */, A.DVY_RQR_DT /* 배송요청일 */, A.ODR_QTTT, /* 발주수량합계*/ A.ODR_SAM, /* 발주금액합계*/ A.VATTT, A.VCBT_AM, A.VCBX_AM, A.DDLY_YN/* 직송여부 */, A.NA_ODR_STS_DSC /* 발주상태 */, A.RVOPL_NA_TRPL_C/* 수주처 코드 */ , FN_TRPL_CIF_NM (A.RVOPL_NA_TRPL_C) AS RVOPL_NA_TRPL_N /* 수주처명 */ , A.RVOPL_NA_TEAM_C, FN_CM_TEAM_NM (A.RVOPL_NA_TRPL_C, A.RVOPL_NA_TEAM_C) AS RVOPL_NA_TEAM_N /* 수주처팀명 */, '['|| A.ROGO_INF_CRT_DSC || ']' || FN_CM_SIMP_CNM ('ROGO_INF_CRT_DSC', A.ROGO_INF_CRT_DSC) AS ROGO_INF_CRT_DSC/* 수발주정보생성구분코드 */, A.DVYAA_NA_TRPL_C/* 배송처코드 */, FN_TRPL_CIF_NM (A.DVYAA_NA_TRPL_C) AS DVYAA_NA_TRPL_N /* 배송처명 */ , A.DVYAA_NA_TEAM_C/* 배송처팀코드 */ , FN_TRPL_CIF_NM (A.DVYAA_NA_TEAM_C) AS DVYAA_NA_TEAM_N /* 배송처팀명 */ , A.SPYPL_NA_TRPL_C, A.SPYPL_NA_TEAM_C, C.CSER_CTR_TPC C_CSER_CTR_TPC, C.PRGR_C /* 가격군코드 */, C.CSER_NA_USR_SYS_KDC /* 발주처시스템코드 */, A.SPY_TPC, CASE WHEN A.DDLY_YN = '1' THEN '직송' ELSE '일반' END AS DDLY_YN_N FROM TB_OD_ORDER_M A, TB_OD_ORDER_D B, TB_TR_TRNREL C WHERE 1 = 1
AND A.ODR_SLPNO = #{ODR_SLPNO}
AND A.RVOPL_NA_TRPL_C = #{RVOPL_NA_TRPL_C}
AND A.ODRPL_NA_TRPL_C = #{ODRPL_NA_TRPL_C}
AND FN_TRPL_CIF_NM (A.ODRPL_NA_TRPL_C) LIKE #{ODRPL_NA_TRPL_N}||'%'
AND A.DVY_RQR_DT BETWEEN #{DVY_RQR_DT_FROM} AND #{DVY_RQR_DT_TO}
AND A.DDLY_YN = #{DDLY_YN}
AND A.DDLY_YN = #{DDLY_YN}
AND A.DEL_DTM IS NULL AND B.DEL_DTM IS NULL AND A.ODRPL_NA_TRPL_C = B.ODRPL_NA_TRPL_C AND A.ODRPL_NA_TEAM_C = B.ODRPL_NA_TEAM_C AND A.ODR_DT = B.ODR_DT AND A.ODR_SLPNO = B.ODR_SLPNO AND A.ODRPL_NA_TRPL_C = C.CSER_NA_TRPL_C(+) AND A.ODRPL_NA_TEAM_C = C.CSER_NA_TEAM_C(+) AND A.RVOPL_NA_TRPL_C = C.SPLR_NA_TRPL_C(+) AND A.RVOPL_NA_TEAM_C = C.SPLR_NA_TEAM_C (+) AND A.SPY_TPC = C.SPY_TPC (+) AND A.MNGT_NA_TRPL_C = C.MNGT_NA_TRPL_C(+) AND A.MNGT_NA_TEAM_C = C.MNGT_NA_TEAM_C(+) AND A.MNGT_NA_TR_TPC = C.MNGT_NA_TR_TPC(+) AND A.PRC_YN IN ('R') AND A.NA_ODR_STS_DSC = '03' ORDER BY A.ODR_DT DESC, A.ODR_SLPNO DESC

