SELECT A.SPYPL_NA_TRPL_C, A.RVOPL_NA_TRPL_C, A.RVOPL_NA_TEAM_C, B.NA_WRS_C, C.WRSNM, C.WRS_STDNM, /* 규격 */ COUNT(DISTINCT A.SPYPL_NA_TRPL_C||A.DVYAA_NA_TRPL_C||A.SPYPL_NA_TRPL_C) AS CNT, SUM(B.DVY_PLA_QT) AS DVY_PLA_QT, SUM(B.DVY_AM) AS DVY_AM, SUM(B.DVY_VAT) AS DVY_VAT, MIN(FN_TRPL_CIF_NM(A.SPYPL_NA_TRPL_C)) AS SPYPL_NA_TRPL_N, MIN(FN_TRPL_CIF_NM(A.RVOPL_NA_TRPL_C)) AS RVOPL_NA_TRPL_N, A.GRS_SOJU_YN, A.VHCNO, MIN(FN_CM_SIMP_CNM('DVY_PLASH_STSC',A.DVY_PLASH_STSC)) AS DVY_PLASH_STAC /* MIN(CASE WHEN B.NA_DVY_PLASH_SLPNO IS NULL THEN '03 매입' ELSE '01 배송예정' END) AS DVY_PLASH_STAC 배송예정서상태코드 */ FROM TB_OD_DLVPNSHT_M A, TB_OD_DLVPNSHT_D B, TB_GD_WRS C WHERE 1=1 AND B.SPYPL_NA_TRPL_C = A.SPYPL_NA_TRPL_C AND B.DVYAA_NA_TRPL_C = A.DVYAA_NA_TRPL_C AND B.NA_DVY_PLASH_SLPNO = A.NA_DVY_PLASH_SLPNO AND B.NA_WRS_C = C.NA_WRS_C(+) AND B.WK_DS != 'D' AND A.TEMP_YN='N' AND A.PRC_YN IN ('R', 'Y')
