 DM.NA_WRS_MCLC, DM.VHCNO, DM.DDLY_YN, CIF.NA_USR_SYS_KDC, BM.SLP_DT, BM.NA_SLPNO, DD.DVY_PLA_QT, DD.DVY_AM, DD.DVY_VAT, DD.DVY_VCBT_AM, DD.DVY_SSDY, DD.DVY_TROT_FEE, DD.DVY_PHD_FEE, DD.DVY_FAR_ASTCS, BM.NA_BZPLC, BM.NA_TEAM_C, TRN.PRGR_C, DM.DVY_PLASH_KDC, DM.DVY_PLASH_STSC, DM.OGN_NA_DVY_PLASH_SLPNO, DM.RMK_CNTN, DM.FSRGMN_ENO, DM.LSCHG_DTM, DM.LS_CMENO, DD.DVY_SSDYRT, DM.GRS_SOJU_YN, DD.DVY_PLA_SQNO FROM TB_OD_DLVPNSHT_M DM ,TB_OD_DLVPNSHT_D DD ,TB_BY_BYNG_M BM ,TB_TR_TRNREL TRN ,TB_TR_TRPL_CIF CIF WHERE 1=1 AND DM.SPYPL_NA_TRPL_C = DD.SPYPL_NA_TRPL_C AND DM.DVYAA_NA_TRPL_C = DD.DVYAA_NA_TRPL_C AND DM.NA_DVY_PLASH_SLPNO = DD.NA_DVY_PLASH_SLPNO AND DM.SPYPL_NA_TRPL_C = BM.ACTL_BUYPL_NA_TRPL_C(+) AND DM.NA_DVY_PLASH_SLPNO = BM.NA_DVY_PLASH_SLPNO(+) AND DM.ODRPL_NA_TRPL_C = BM.NA_BZPLC(+) AND DM.ODRPL_NA_TRPL_C = CIF.NA_TRPL_C AND DM.ODRPL_NA_TRPL_C = TRN.CSER_NA_TRPL_C(+) AND DM.ODRPL_NA_TEAM_C = TRN.CSER_NA_TEAM_C(+) AND DM.SPYPL_NA_TRPL_C = TRN.SPLR_NA_TRPL_C(+) AND DM.SPYPL_NA_TEAM_C = TRN.SPLR_NA_TEAM_C(+) AND DM.CSER_CTR_TPC = TRN.CSER_CTR_TPC(+) AND BM.DEL_DTM IS NULL AND DD.WK_DS != 'D' AND DM.TEMP_YN = 'N' AND DM.PRC_YN IN ('Y', 'R')
