) AA, TB_BY_BYNG_WRS BB WHERE 1=1 AND AA.NA_BZPLC = BB.NA_BZPLC(+) AND AA.NA_TEAM_C = BB.NA_TEAM_C(+) AND AA.SLP_DT = BB.SLP_DT(+) AND AA.NA_SLPNO = BB.NA_SLPNO(+) /* AND AA.NA_WRS_C = BB.NA_WRS_C(+) */ AND AA.DVY_PLA_SQNO = BB.SLP_SQNO(+) GROUP BY AA.SPYPL_NA_TRPL_C, AA.SPYPL_NA_TEAM_C, AA.DVY_PLA_DT, AA.NA_DVY_PLASH_SLPNO, AA.ODRPL_NA_TRPL_C, AA.ODRPL_NA_TEAM_C, AA.RVOPL_NA_TRPL_C, AA.RVOPL_NA_TEAM_C, AA.ADJPL_NA_TRPL_C, AA.ADJPL_NA_TEAM_C, AA.MNGT_NA_TRPL_C, AA.MNGT_NA_TEAM_C, AA.DVYAA_NA_TRPL_C, AA.DVYAA_NA_TEAM_C, AA.DVYAA_C, AA.CSER_CTR_TPC, AA.SPY_TPC, AA.SLGT_ETR_DSC, AA.NA_WRS_LCLC, AA.NA_WRS_MCLC, AA.VHCNO, AA.DDLY_YN, AA.NA_USR_SYS_KDC, AA.SLP_DT, AA.NA_SLPNO, AA.NA_BZPLC, AA.NA_TEAM_C, AA.PRGR_C, AA.DVY_PLASH_KDC, AA.DVY_PLASH_STSC, AA.OGN_NA_DVY_PLASH_SLPNO, AA.RMK_CNTN, AA.FSRGMN_ENO, AA.LSCHG_DTM, AA.LS_CMENO, AA.GRS_SOJU_YN ORDER BY AA.SPYPL_NA_TRPL_C, AA.DVY_PLA_DT, AA.ODRPL_NA_TRPL_C

SELECT AA.*, BB.* FROM( SELECT LIST.* FROM ( SELECT A.SPYPL_NA_TRPL_C, A.RVOPL_NA_TRPL_C, FN_TRPL_CIF_NM(A.RVOPL_NA_TRPL_C) AS RVOPL_NA_TRPL_N, A.DVYAA_NA_TRPL_C, FN_TRPL_CIF_NM(A.DVYAA_NA_TRPL_C) AS DVYAA_NA_TRPL_N, A.NA_DVY_PLASH_SLPNO,/* 배송예정서번호 */ A.DVY_PLA_DT,/* 배송예정일 */ A.DVY_AM, /* 배송예정금액 */ A.DVY_VAT, /* 부가세 */ A.CSER_CTR_TPC || ' ' || FN_CM_SIMP_CNM('CSER_CTR_TPC', A.CSER_CTR_TPC) AS CSER_CTR_TPC,/* 수요자계약유형 */ FN_TRPL_CIF_NM(A.SPYPL_NA_TRPL_C) AS SPYPL_NA_TRPL_N, A.FSRGMN_ENO,/* 최초등록자 */ A.DVY_SSDY, /* 배송장려금 */ NVL(( SELECT NVL(SUM(DVY_SSDYRT), 0) / COUNT(*) FROM TB_OD_DLVPNSHT_D WHERE 1=1 AND SPYPL_NA_TRPL_C = A.SPYPL_NA_TRPL_C AND DVYAA_NA_TRPL_C = A.DVYAA_NA_TRPL_C AND NA_DVY_PLASH_SLPNO = A.NA_DVY_PLASH_SLPNO GROUP BY SPYPL_NA_TRPL_C, DVYAA_NA_TRPL_C, NA_DVY_PLASH_SLPNO ), 0) AS DVY_SSDYRT, /* 배송장려금율 */ NVL(B.BYAM, 0) AS BYAM,/* 매입확정금액 */ NVL(C.BYNG_QT, 0) AS BYNG_QT, /* 매입확정수량 */ B.SLP_DT, /* 매입전표일자 */ B.NA_SLPNO, /* 매입전표번호 */ A.ODRPL_NA_TRPL_C, FN_TRPL_CIF_NM(A.ODRPL_NA_TRPL_C) AS ODRPL_NA_TRPL_N, A.DDLY_YN, /* 직송여부 */ '['|| A.ODRPL_NA_TEAM_C ||']'||FN_CM_TEAM_NM(A.ODRPL_NA_TRPL_C, A.ODRPL_NA_TEAM_C) AS ODRPL_NA_TEAM_N , CASE WHEN C.NA_BZPLC IS NULL THEN A.DVY_PLA_QT ELSE 0 END AS STS_DVY_PLA_QT,/* 배송예정수량 */ CASE WHEN C.NA_BZPLC IS NULL THEN A.DVY_AM ELSE 0 END AS STS_DVY_AM, /* 배송예정금액 */ NVL(B.BYAM, 0) AS STS_BYAM,/* 매입금액 */ NVL(C.BYNG_QT, 0) AS STS_BYNG_QT, /* 매입수량 */ A.GRS_SOJU_YN, /* 지방소주여부 */ CASE WHEN B.NA_SLPNO IS NULL THEN '1' /* 배송예정 */ ELSE '2'/* 매입 */ END AS DVY_PLA_STS /* 배송예정서상태 */ FROM TB_OD_DLVPNSHT_M A, TB_BY_BYNG_M B, ( SELECT NA_BZPLC, NA_TEAM_C, SLP_DT, NA_SLPNO, SLP_SQNO, SUM(BYNG_QT) AS BYNG_QT FROM TB_BY_BYNG_WRS GROUP BY NA_BZPLC, NA_TEAM_C, SLP_DT, NA_SLPNO, SLP_SQNO ) C WHERE 1=1 AND A.NA_DVY_PLASH_SLPNO = B.NA_DVY_PLASH_SLPNO(+) AND B.NA_BZPLC = C.NA_BZPLC(+) AND B.NA_TEAM_C = C.NA_TEAM_C(+) AND B.SLP_DT = C.SLP_DT(+) AND B.NA_SLPNO = C.NA_SLPNO(+) AND B.DEL_DTM IS NULL AND A.TEMP_YN = 'N' AND A.PRC_YN IN ('Y', 'R')
AND A.NA_DVY_PLASH_SLPNO = #{na_dvy_plash_slpno}
AND A.ODRPL_NA_TRPL_C = #{odrpl_na_trpl_c}
AND A.CSER_CTR_TPC = #{cser_ctr_tpc}
AND A.DDLY_YN = #{ddly_yn}
AND A.DVY_PLA_DT BETWEEN #{dvy_pla_dt_from} AND #{dvy_pla_dt_to}
AND A.GRS_SOJU_YN = #{ngrs_soju_yn}
AND A.SPYPL_NA_TRPL_C IN
#{item}
) LIST WHERE 1=1
AND AA.DVY_PLA_STS = #{dvy_pla_sts_rdo}
) AA, ( SELECT A.NA_WRS_C AS B_NA_WRS_C, C.WRSNM AS B_WRSNM, A.ODR_DT AS B_ODR_DT, A.ODR_SLPNO AS B_ODR_SLPNO, A.ODR_DSQNO AS B_ODR_DSQNO, A.TXT_DSC AS B_TXT_DSC, /* 과세구분 */ TW.PD_YY AS B_PD_YY, /* 생산년도 */ A.DVY_WRS_UPR AS B_DVY_WRS_UPR, /* 배송상품단가 */ A.DVY_PLA_QT AS B_DVY_PLA_QT, /* 배송예정수량 */ A.DVY_AM AS B_DVY_AM, /* 배송금액 */ A.DVY_VAT AS B_DVY_VAT, /* 배송부가세 */ A.DVY_VCBT_AM AS B_DVY_VCBT_AM, /* 배송공병금액 */ ( CASE WHEN A.DVY_SSDYRT <> 0 AND A.DVY_SSDY = 0 THEN ( A.DVY_AM * A.DVY_SSDYRT ) / 100 ELSE A.DVY_SSDY END ) AS B_DVY_SSDY, /* 배송장려금 */ A.DVY_TROT_FEE AS B_DVY_TROT_FEE, /* 배송환급수수료 */ A.DVY_PHD_FEE AS B_DVY_PHD_FEE, /* 배송물류수수료 */ A.DVY_FAR_ASTCS AS B_DVY_FAR_ASTCS, /* 배송운임보조비 */ E.BYNG_UPR AS B_BYNG_UPR, /* 매입확정단가 */ E.BYNG_QT AS B_BYNG_QT, /* 매입확정수량 */ E.BYNG_WT AS B_BYNG_WT, /* 매입확정중량 */ E.BYAM AS B_BYAM,/* 매입확정금액 */ A.NA_DVY_PLASH_SLPNO AS B_NA_DVY_PLASH_SLPNO, A.DVYAA_NA_TRPL_C AS B_DVYAA_NA_TRPL_C, A.SPYPL_NA_TRPL_C AS B_SPYPL_NA_TRPL_C, CASE WHEN D.NA_DVY_PLASH_SLPNO IS NULL THEN '03 매입' ELSE '01 배송예정' END AS B_DVY_PLASH_STAC, /* 배송예정서상태코드 */ A.DVY_SSDYRT AS B_DVY_SSDYRT /* 배송장려금율 */ FROM TB_OD_DLVPNSHT_D A, TB_OD_DLVPNSHT_M B, TB_GD_WRS C, TB_OD_TRTWRS TW, TB_BY_BYNG_M D, TB_BY_BYNG_WRS E WHERE 1=1 AND A.SPYPL_NA_TRPL_C = B.SPYPL_NA_TRPL_C AND A.DVYAA_NA_TRPL_C = B.DVYAA_NA_TRPL_C AND A.NA_DVY_PLASH_SLPNO = B.NA_DVY_PLASH_SLPNO AND A.NA_WRS_C = C.NA_WRS_C(+) AND A.NA_DVY_PLASH_SLPNO = D.NA_DVY_PLASH_SLPNO(+) AND D.NA_BZPLC = E.NA_BZPLC(+) AND D.NA_TEAM_C = E.NA_TEAM_C(+) AND D.SLP_DT = E.SLP_DT(+) AND D.NA_SLPNO = E.NA_SLPNO(+) AND A.SPYPL_NA_TRPL_C = TW.NA_BZPLC(+) AND A.NA_WRS_C = TW.NA_WRS_C(+) AND B.TEMP_YN = 'N' --AND A.WK_DS != 'D' AND B.PRC_YN IN ('Y', 'R')
AND A.SPYPL_NA_TRPL_C = #{g_spypl_na_trpl_c}
AND A.DVYAA_NA_TRPL_C = #{g_dvyaa_na_trpl_c}
AND A.NA_DVY_PLASH_SLPNO = #{g_na_dvy_plash_slpno}
) BB WHERE 1=1 AND AA.SPYPL_NA_TRPL_C = BB.B_SPYPL_NA_TRPL_C AND AA.DVYAA_NA_TRPL_C = BB.B_DVYAA_NA_TRPL_C AND AA.NA_DVY_PLASH_SLPNO = BB.B_NA_DVY_PLASH_SLPNO

SELECT A.* ,D.VCBT_UPR FROM (
SELECT /*+ index(TB_BY_BYNG_M IDX_BY_BYNG_M_05) */ DM.SPYPL_NA_TRPL_C, /* 0 */ DM.SPYPL_NA_TEAM_C, DM.RVOPL_NA_TRPL_C, FN_TRPL_CIF_NM(DM.RVOPL_NA_TRPL_C) AS RVOPL_NA_TRPL_N, DM.DVYAA_NA_TRPL_C, FN_TRPL_CIF_NM(DM.DVYAA_NA_TRPL_C) AS DVYAA_NA_TRPL_N, DM.NA_DVY_PLASH_SLPNO, DM.DVY_PLA_DT, DM.ODRPL_NA_TRPL_C, FN_TRPL_CIF_NM(DM.ODRPL_NA_TRPL_C) AS ODRPL_NA_TRPL_N, DM.ODRPL_NA_TEAM_C, DM.DVY_AM AS TOTAL_DVY_AM, DM.DVY_VAT AS TOTAL_DVY_VAT, DM.DVY_PLASH_STSC || ' ' || FN_CM_SIMP_CNM('DVY_PLASH_STSC', DM.DVY_PLASH_STSC) AS DVY_PLASH_STSC, DM.RMK_CNTN, DD.NA_WRS_C, WRS.WRSNM, WRS.WRS_STDNM, WRS.NA_WRS_UNT_C, FN_CM_SIMP_CNM('NA_WRS_UNT_C', WRS.NA_WRS_UNT_C) AS NA_WRS_UNT_C_NM, WRS.BOXPE_AQZ, DD.DVY_WRS_UPR, DD.DVY_PLA_QT, DD.DVY_AM, DD.DVY_VAT, DD.DVY_VCBT_AM, DD.DVY_PLA_SQNO, DM.CSER_CTR_TPC, (DD.ODR_DT || '-' || DD.ODR_SLPNO || '-' || DD.ODR_DSQNO) AS ODR_SLPNO
FROM TB_OD_DLVPNSHT_M DM , TB_OD_DLVPNSHT_D DD , TB_BY_BYNG_M BM , TB_TR_TRNREL TRN , TB_TR_TRPL_CIF CIF, TB_GD_WRS WRS
WHERE 1=1 AND DM.SPYPL_NA_TRPL_C = DD.SPYPL_NA_TRPL_C AND DM.DVYAA_NA_TRPL_C = DD.DVYAA_NA_TRPL_C AND DM.NA_DVY_PLASH_SLPNO = DD.NA_DVY_PLASH_SLPNO AND DM.SPYPL_NA_TRPL_C = BM.ACTL_BUYPL_NA_TRPL_C(+) AND DM.ODRPL_NA_TRPL_C = BM.NA_BZPLC(+) AND DM.ODRPL_NA_TRPL_C = CIF.NA_TRPL_C(+) AND DM.NA_DVY_PLASH_SLPNO = BM.NA_DVY_PLASH_SLPNO(+) AND 1 = BM.NA_CRC_CAN_DSC(+) AND DM.ODRPL_NA_TRPL_C = TRN.CSER_NA_TRPL_C (+) AND DM.ODRPL_NA_TEAM_C = TRN.CSER_NA_TEAM_C (+) AND DM.SPYPL_NA_TRPL_C = TRN.SPLR_NA_TRPL_C (+) AND DM.SPYPL_NA_TEAM_C = TRN.SPLR_NA_TEAM_C (+) AND DM.CSER_CTR_TPC = TRN.CSER_CTR_TPC (+) AND DD.NA_WRS_C = WRS.NA_WRS_C AND BM.DEL_DTM IS NULL AND DM.TEMP_YN = 'N' AND DD.WK_DS != 'D' AND DM.PRC_YN IN ('Y', 'R')
AND DM.TRU_AGCY_NA_TRPL_C = #{TRU_AGCY_NA_TRPL_C}
AND DM.NA_DVY_PLASH_SLPNO = #{na_dvy_plash_slpno}
AND DM.ODRPL_NA_TRPL_C = #{odrpl_na_trpl_c}
AND DM.CSER_CTR_TPC = #{cser_ctr_tpc}
AND DM.DDLY_YN = #{ddly_yn}
AND DM.DVY_PLA_DT BETWEEN #{dvy_pla_dt_from} AND #{dvy_pla_dt_to}
AND DM.GRS_SOJU_YN = #{ngrs_soju_yn}
AND DM.SPYPL_NA_TRPL_C IN
#{item}
AND DM.DVY_PLASH_STSC = #{dvy_pla_sts_rdo}
) A , TB_OD_TRTWRS D WHERE 1=1 AND A.NA_WRS_C = D.NA_WRS_C(+) AND A.ODRPL_NA_TRPL_C = D.NA_BZPLC(+)
ORDER BY A.NA_DVY_PLASH_SLPNO, A.DVY_PLA_SQNO, A.DVY_PLA_DT, A.RVOPL_NA_TRPL_C, A.SPYPL_NA_TRPL_C, A.ODR_SLPNO
ORDER BY A.DVY_PLA_DT, A.SPYPL_NA_TRPL_C, A.DVYAA_NA_TRPL_C, A.NA_DVY_PLASH_SLPNO, A.ODR_SLPNO
ORDER BY A.DVYAA_NA_TRPL_C, A.DVY_PLA_DT, A.SPYPL_NA_TRPL_C, A.NA_DVY_PLASH_SLPNO, A.ODR_SLPNO

SELECT A.*, ROWNUM FROM ( SELECT /*+ INDEX(A XPK_TB_OD_DLVPNSHT_M) */ 'I' rec_sts /* , ( SELECT COUNT(*) FROM TB_OD_DLVPNSHT_D X WHERE A.spypl_na_trpl_c = X.spypl_na_trpl_c AND A.dvyaa_na_trpl_c = X.dvyaa_na_trpl_c AND A.na_dvy_plash_slpno= X.na_dvy_plash_slpno ) cnt */ , COUNT(8) OVER (PARTITION BY A.SPYPL_NA_TRPL_C,A.DVYAA_NA_TRPL_C, A.NA_DVY_PLASH_SLPNO ) CNT , A.SPYPL_NA_TRPL_C /* 03 */ , '' AS C04 /* 04 공급처팀코드*/ , A.DVY_PLA_DT /* 05 */ , '' AS NA_DVY_PLASH_SLPNO /* 06 */ , A.ODRPL_NA_TRPL_C /* 07 */ , A.ODRPL_NA_TEAM_C /* 08 */ , A.RVOPL_NA_TRPL_C /* 09 */ , A.RVOPL_NA_TEAM_C /* 10 */ , '' AS ADJPL_NA_TRPL_C /* 11 */ , '' AS ADJPL_NA_TEAM_C /* 12 */ , A.MNGT_NA_TR_TPC /* 13 */ , A.MNGT_NA_TRPL_C /* 14 */ , A.MNGT_NA_TEAM_C /* 15 */ , A.DVYAA_NA_TRPL_C /* 16 */ , A.DVYAA_NA_TEAM_C /* 17 */ , A.DVYAA_C /* 18 */ , A.CSER_CTR_TPC /* 19 */ , A.SPY_TPC /* 20 */ , A.SLGT_ETR_DSC /* 21 */ , A.NA_WRS_LCLC /* 22 */ , A.NA_WRS_MCLC /* 23 */ , '' AS C33/* 33 인수도코드*/ , A.DVY_PLA_QT/* 39 */ , A.DVY_AM/* 41 */ , A.DVY_VAT /* 42 */ , A.VCBT_AM /* 43 */ , A.VCBX_AM /* 44 */ , A.DVY_SSDY /* 45 */ , A.DVY_TROT_FEE /* 46 */ , A.DVY_PHD_FEE /* 47 */ , A.DVY_FAR_ASTCS /* 48 */ , '' AS C59/* 59 목재파렛트수량 */ , '' AS C60/* 60 플라스틱파렛트수량 */ , '' AS C61/* 61 가격군코드 */ , '' AS C62/* 62 구매단가구분코드 */ , '' AS C63/* 63 매입단가구분코드 */ , A.RMK_CNTN/* 69 */ , B.DVY_PLA_SQNO /* 06 */ , B.NA_WRS_C /* 07 */ , '' AS C10/* 10 경제통합출하자코드 */ , '' AS C11/* 11산지경제통합사업장코드 */ , '' AS C12/* 12산지경제통합팀코드 */ , '' AS C13/* 13수령인명 */ , '' AS C14/* 14수령인전화번호 */ , B.ODR_DT /* 18 */ , B.ODR_SLPNO /* 19 */ , B.ODR_DSQNO /* 20 */ , B.TXT_DSC /* 21 */ , B.DVY_WRS_UPR /* 25 */ , B.DVY_PLA_QT AS DVY_PLA_QT_D /* 26 */ , B.DVY_AM AS DVY_AM_D/* 28 */ , B.DVY_VAT AS DVY_VAT_D/* 29 */ , B.DVY_VCBT_AM /* 30 */ , B.DVY_SSDY AS DVY_SSDY_D/* 31 */ , B.DVY_TROT_FEE AS DVY_TROT_FEE_D/* 32 */ , B.DVY_PHD_FEE AS DVY_PHD_FEE_D/* 33 */ , B.DVY_FAR_ASTCS AS DVY_FAR_ASTCS_D/* 34 */ , '' AS C38/* 38비고 내용 */ , '' AS C39/* 40매출단가유형코드 */ , '' AS C41/* 41수요자계약유형코드*/ , '' AS C42/* 42구매사업방식코드*/ FROM TB_OD_DLVPNSHT_M A, TB_OD_DLVPNSHT_D B WHERE 1=1 AND A.spypl_na_trpl_c IN
#{item}
AND A.na_dvy_plash_slpno= #{na_dvy_plash_slpno}
AND A.spypl_na_trpl_c = B.spypl_na_trpl_c AND A.dvyaa_na_trpl_c = B.dvyaa_na_trpl_c AND A.na_dvy_plash_slpno= B.na_dvy_plash_slpno AND A.TEMP_YN='N' AND B.WK_DS != 'D' AND A.PRC_YN = 'R' AND A.DVY_PLA_DT BETWEEN #{dvy_pla_dt_from} AND #{dvy_pla_dt_to} ORDER BY A.SPYPL_NA_TRPL_C, A.DVYAA_NA_TRPL_C, A.NA_DVY_PLASH_SLPNO, B.DVY_PLA_SQNO ) A WHERE ROWNUM <= 100

mapper.sql:
SELECT ( SELECT NA_TRPL_SEAL_FLNM FROM TB_TR_TRPL_CIF WHERE NA_TRPL_C = A.SPYPL_NA_TRPL_C ) AS NA_TRPL_SEAL_FLNM , FN_CM_SIMP_CNM('CSER_CTR_TPC', A.CSER_CTR_TPC) AS CSER_CTR_TPC /* 계약유형 */ , C.CLNTNM AS SPYPL_NA_TRPL_N /* 공급처 상호 */ , A.SPYPL_NA_TRPL_C /* 공급처 코드 */ , C.BIZ_NUMBER AS SPYPL_BIZ_NUMBER /* 사업자 번호 */ , C.REPMNM AS SPYPL_REPMNM /* 대표자 */ , C.DONGUP || ' ' || C.DONGBW AS SPYPL_ADDRESS /* 주소 */ , C.ATEL || '-' || C.HTEL || '-' || C.STEL AS SPYPL_TEL /* 전화번호 */ , D.CLNTNM AS DVYAA_NA_TRPL_N /* 배송처 상호 */ , A.DVYAA_NA_TRPL_C /* 배송처코드 */ , D.BIZ_NUMBER AS DVYAA_BIZ_NUMBER /* 사업자 번호 */ , D.REPMNM AS DVYAA_REPMNM /* 대표자 */ , A.DVY_PLA_DT AS CNR_DT , B.DVY_PLA_SQNO , B.NA_WRS_C , WRS.WRSNM , WRS.WRS_STDNM AS NA_WRS_UNT_N /* 규격 */ , CASE WHEN D.NA_USR_SYS_KDC IN ('52','54') THEN ( SELECT G.MIN_ODR_QT FROM TB_OD_TRTWRS G WHERE G.NA_BZPLC = A.ODRPL_NA_TRPL_C AND G.NA_WRS_C = B.NA_WRS_C AND ROWNUM = 1 ) ELSE WRS.BOXPE_AQZ END AS BOXPE_AQZ /* 입수 */ , CASE WHEN D.NA_USR_SYS_KDC IN ('52','54') THEN ( SELECT ROUND(B.DVY_PLA_QT / CASE G.MIN_ODR_QT WHEN 0 THEN 1 ELSE G.MIN_ODR_QT END) FROM TB_OD_TRTWRS G WHERE G.NA_BZPLC = A.ODRPL_NA_TRPL_C AND G.NA_WRS_C = B.NA_WRS_C AND ROWNUM = 1 ) ELSE ROUND(B.DVY_PLA_QT / WRS.BOXPE_AQZ, 2) END AS BOX_QT /* 박스 수량 */ , B.DVY_PLA_QT /* 낱개수량 */ , B.DVY_WRS_UPR /* 단가 */ , B.DVY_AM /* 금액 */ , B.DVY_VAT /* 부가세 */ , (B.DVY_AM - B.DVY_VAT) AS SPYPL_AM /* 공급금액 */ , B.DVY_SSDY /* 장려금 */ , CASE B.DQPD_RSNC WHEN '99' THEN B.DQPD_RSN_CNTN || ' (' || B.DQPD_RSNC || ')' ELSE CASE WHEN B.DQPD_RSNC IS NOT NULL THEN FN_CM_SIMP_CNM('DQPD_RSNC', B.DQPD_RSNC) || ' (' || B.DQPD_RSNC || ')' ELSE ' ' END END AS DQPD_RSN /* 결품사유코드가 99일경우 수기등록 결품사유기타내용 보여줌*/ , CASE B.DSTR_TER_MRK_DSC WHEN '1' THEN '유통기한' WHEN '2' THEN '제조일자' ELSE '해당없음' END AS DSTR_TER_MRK_DSC , B.DSTR_TER_DT , CASE WHEN D.NA_USR_SYS_KDC IN ('52','54') THEN ( SELECT CASE WHEN G.PHD_FCLT_C = 'DC' THEN 'DC' WHEN G.PHD_FCLT_C = 'TC' AND G.SRTR_PRC_YN = '1' THEN 'TS' WHEN G.PHD_FCLT_C = 'TC' AND G.SRTR_PRC_YN = '0' THEN 'TB' ELSE '' END FROM TB_FD_GM_GOOD_WH G WHERE G.NA_BZPLC = A.ODRPL_NA_TRPL_C AND G.NA_WRS_C = B.NA_WRS_C AND ROWNUM = 1 ) ELSE '' END AS BKG_EVT_DSC , CASE WHEN D.NA_USR_SYS_KDC = '54' THEN ( SELECT BAS_WRS_QT || ':' || DUM_QT FROM TB_FD_GM_GOOD_DUM WHERE NA_BZPLC = A.ODRPL_NA_TRPL_C AND NA_WRS_C = B.NA_WRS_C AND DUM_DSC = '1' AND PRGR_C = '00' AND DEL_DTM IS NULL AND A.DVY_PLA_DT BETWEEN APL_DT AND ED_DT ) ELSE '' END AS DUM_RAT FROM TB_OD_DLVPNSHT_M A INNER JOIN TB_OD_DLVPNSHT_D B ON A.NA_DVY_PLASH_SLPNO = B.NA_DVY_PLASH_SLPNO AND A.DVYAA_NA_TRPL_C = B.DVYAA_NA_TRPL_C AND A.SPYPL_NA_TRPL_C = B.SPYPL_NA_TRPL_C INNER JOIN TB_TR_TRPL_CIF C ON A.SPYPL_NA_TRPL_C = C.NA_TRPL_C INNER JOIN TB_TR_TRPL_CIF D ON A.DVYAA_NA_TRPL_C = D.NA_TRPL_C INNER JOIN TB_TR_TRPL_CIF G ON A.ODRPL_NA_TRPL_C = G.NA_TRPL_C INNER JOIN TB_GD_WRS WRS ON B.NA_WRS_C = WRS.NA_WRS_C WHERE A.TEMP_YN = 'N' AND B.WK_DS != 'D' AND A.NA_DVY_PLASH_SLPNO = #{NA_DVY_PLASH_SLPNO} AND A.SPYPL_NA_TRPL_C = #{SPYPL_NA_TRPL_C} AND A.DVYAA_NA_TRPL_C = #{DVYAA_NA_TRPL_C}
AND A.TRU_AGCY_NA_TRPL_C = #{TRU_AGCY_NA_TRPL_C}
ORDER BY BKG_EVT_DSC, WRS.WRSNM
ORDER BY BKG_EVT_DSC, B.NA_WRS_C
ORDER BY BKG_EVT_DSC, DVY_PLA_SQNO

mapper.update:
UPDATE TB_OD_DLVPNSHT_M
SET
	DVY_PLASH_STSC='06',
	WK_DS = 'D',
	PRC_YN='N',
	LSCHG_DTM=SYSDATE,
	LS_CMENO= #{LS_CMENO},
	EDI_IF_ID = NULL, EDI_IF_DT = NULL, EDI_IF_RSND_SEQ = 0, DOC_RMS_LOC_DSC='WEB'
WHERE 1=1
	AND SPYPL_NA_TRPL_C = #{SPYPL_NA_TRPL_C}
	AND DVYAA_NA_TRPL_C = #{DVYAA_NA_TRPL_C}
	AND NA_DVY_PLASH_SLPNO=#{NA_DVY_PLASH_SLPNO}

		AND DVY_PLA_DT=#{DVY_PLA_DT}

UPDATE TB_OD_DLVPNSHT_D
SET
	WK_DS='D'
WHERE 1=1
	AND SPYPL_NA_TRPL_C = #{SPYPL_NA_TRPL_C}
	AND DVYAA_NA_TRPL_C = #{DVYAA_NA_TRPL_C}
	AND NA_DVY_PLASH_SLPNO=#{NA_DVY_PLASH_SLPNO}

mapper.select:
SELECT
 A.*
FROM TB_OD_DLVPNSHT_M A
WHERE A.SPYPL_NA_TRPL_C=#{SPYPL_NA_TRPL_C}
AND A.DVYAA_NA_TRPL_C=#{DVYAA_NA_TRPL_C}
AND A.NA_DVY_PLASH_SLPNO=#{NA_DVY_PLASH_SLPNO}

	AND A.DVY_PLA_DT=#{DVY_PLA_DT}

SELECT
 A.*
FROM TB_OD_DLVPNSHT_D A
WHERE A.SPYPL_NA_TRPL_C=#{SPYPL_NA_TRPL_C}
AND A.DVYAA_NA_TRPL_C=#{DVYAA_NA_TRPL_C}
AND A.NA_DVY_PLASH_SLPNO=#{NA_DVY_PLASH_SLPNO}

	AND A.DVY_PLA_DT=#{DVY_PLA_DT}

mapper.insert:
{call declare begin
